import React, { Component } from "react";
import TutorialDataService from "../services/tutorial.service";

export default class Tutorial extends Component {
  constructor(props) {
    super(props);
    this.onChangeTitle = this.onChangeTitle.bind(this);
    this.onChangeDescription = this.onChangeDescription.bind(this);
    this.onChangeVenueName = this.onChangeVenueName.bind(this);
    this.onChangeVenueAddress = this.onChangeVenueAddress.bind(this);
    this.onChangeDateOfEvent = this.onChangeDateOfEvent.bind(this);
    this.onChangeStartTime = this.onChangeStartTime.bind(this);
    this.onChangeEndTime = this.onChangeEndTime.bind(this);
    this.getTutorial = this.getTutorial.bind(this);
    this.updatePublished = this.updatePublished.bind(this);
    this.updateTutorial = this.updateTutorial.bind(this);
    this.deleteTutorial = this.deleteTutorial.bind(this);

    this.state = {
      currentTutorial: {
        id: null,
        title: "",
        description: "",
        venueName: "",
        venueAddress: "",
        dateOfEvent: "",
        startTime: "",
        endTime: "",
        published: false
      },
      message: ""
    };
  }

  componentDidMount() {
    this.getTutorial(this.props.match.params.id);
  }

  onChangeTitle(e) {
    const title = e.target.value;

    this.setState(function(prevState) {
      return {
        currentTutorial: {
          ...prevState.currentTutorial,
          title: title
        }
      };
    });
  }

  // 
  onChangeVenueName(e) {
    const venueName = e.target.value;

    this.setState(function(prevState) {
      return {
        currentTutorial: {
          ...prevState.currentTutorial,
          venueName: venueName
        }
      };
    });
  }

  onChangeVenueAddress(e) {
    const venueAddress = e.target.value;

    this.setState(function(prevState) {
      return {
        currentTutorial: {
          ...prevState.currentTutorial,
          venueAddress: venueAddress
        }
      };
    });
  }

  onChangeDateOfEvent(e) {
    const dateOfEvent = e.target.value;

    this.setState(function(prevState) {
      return {
        currentTutorial: {
          ...prevState.currentTutorial,
          dateOfEvent: dateOfEvent
        }
      };
    });
  }

  onChangeStartTime(e) {
    const startTime = e.target.value;

    this.setState(function(prevState) {
      return {
        currentTutorial: {
          ...prevState.currentTutorial,
          startTime: startTime
        }
      };
    });
  }

  onChangeEndTime(e) {
    const endTime = e.target.value;

    this.setState(function(prevState) {
      return {
        currentTutorial: {
          ...prevState.currentTutorial,
          endTime: endTime
        }
      };
    });
  }

  // 

  onChangeDescription(e) {
    const description = e.target.value;
    
    this.setState(prevState => ({
      currentTutorial: {
        ...prevState.currentTutorial,
        description: description
      }
    }));
  }

  getTutorial(id) {
    TutorialDataService.get(id)
      .then(response => {
        this.setState({
          currentTutorial: response.data
        });
        console.log(response.data);
      })
      .catch(e => {
        console.log(e);
      });
  }

  updatePublished(status) {
    var data = {
      id: this.state.currentTutorial.id,
      title: this.state.currentTutorial.title,
      description: this.state.currentTutorial.description,
      venueName: this.state.currentTutorial.venueName,
      venueAddress: this.state.currentTutorial.venueAddress,
      dateOfEvent: this.state.currentTutorial.dateOfEvent,
      startTime: this.state.currentTutorial.startTime,
      endTime: this.state.currentTutorial.endTime,
      published: status
    };

    TutorialDataService.update(this.state.currentTutorial.id, data)
      .then(response => {
        this.setState(prevState => ({
          currentTutorial: {
            ...prevState.currentTutorial,
            published: status
          }
        }));
        console.log(response.data);
      })
      .catch(e => {
        console.log(e);
      });
  }

  updateTutorial() {
    TutorialDataService.update(
      this.state.currentTutorial.id,
      this.state.currentTutorial
    )
      .then(response => {
        console.log(response.data);
        this.setState({
          message: "The tutorial was updated successfully!"
        });
      })
      .catch(e => {
        console.log(e);
      });
  }

  deleteTutorial() {    
    TutorialDataService.delete(this.state.currentTutorial.id)
      .then(response => {
        console.log(response.data);
        this.props.history.push('/tutorials')
      })
      .catch(e => {
        console.log(e);
      });
  }

  render() {
    const { currentTutorial } = this.state;

    return (
      <div>
        {currentTutorial ? (
          <div className="edit-form">
            <h4>Tutorial</h4>
            <form>
              <div className="form-group">
                <label htmlFor="title">Title</label>
                <input
                  type="text"
                  className="form-control"
                  id="title"
                  value={currentTutorial.title}
                  onChange={this.onChangeTitle}
                />
              </div>
            {/*  */}

            <div className="form-group">
                <label htmlFor="venueName">Venue Name</label>
                <input
                  type="text"
                  className="form-control"
                  id="venueName"
                  value={currentTutorial.venueName}
                  onChange={this.onChangeVenueName}
                />
            </div>

            <div className="form-group">
                <label htmlFor="title">Venue Address</label>
                <input
                  type="text"
                  className="form-control"
                  id="venueAddress"
                  value={currentTutorial.venueAddress}
                  onChange={this.onChangeVenueAddress}
                />
            </div>

            <div className="form-group">
                <label htmlFor="dateOfEvent">Date Of Event</label>
                <input
                  type="text"
                  className="form-control"
                  id="dateOfEvent"
                  value={currentTutorial.dateOfEvent}
                  onChange={this.onChangeDateOfEvent}
                />
            </div>

            <div className="form-group">
                <label htmlFor="startTime">Start Time</label>
                <input
                  type="text"
                  className="form-control"
                  id="startTime"
                  value={currentTutorial.startTime}
                  onChange={this.onChangeStartTime}
                />
            </div>

            <div className="form-group">
                <label htmlFor="endTime">End Time</label>
                <input
                  type="text"
                  className="form-control"
                  id="endTime"
                  value={currentTutorial.endTime}
                  onChange={this.onChangeEndTime}
                />
            </div>

            {/*  */}

              <div className="form-group">
                <label htmlFor="description">Description</label>
                <input
                  type="text"
                  className="form-control"
                  id="description"
                  value={currentTutorial.description}
                  onChange={this.onChangeDescription}
                />
              </div>

              <div className="form-group">
                <label>
                  <strong>Status:</strong>
                </label>
                {currentTutorial.published ? "Published" : "Pending"}
              </div>
            </form>

            {currentTutorial.published ? (
              <button
                className="badge badge-primary mr-2"
                onClick={() => this.updatePublished(false)}
              >
                UnPublish
              </button>
            ) : (
              <button
                className="badge badge-primary mr-2"
                onClick={() => this.updatePublished(true)}
              >
                Publish
              </button>
            )}

            <button
              className="badge badge-danger mr-2"
              onClick={this.deleteTutorial}
            >
              Delete
            </button>

            <button
              type="submit"
              className="badge badge-success"
              onClick={this.updateTutorial}
            >
              Update
            </button>
            <p>{this.state.message}</p>
          </div>
        ) : (
          <div>
            <br />
            <p>Please click on a Tutorial...</p>
          </div>
        )}
      </div>
    );
  }
}
