import React, { Component } from "react";
import TutorialDataService from "../services/tutorial.service";
import TextField from '@material-ui/core/TextField';

export default class AddTutorial extends Component {
  constructor(props) {
    super(props);
    this.onChangeTitle = this.onChangeTitle.bind(this);
    this.onChangeDescription = this.onChangeDescription.bind(this);
    this.onChangeVenueName = this.onChangeVenueName.bind(this);
    this.onChangeVenueAddress = this.onChangeVenueAddress.bind(this);
    this.onChangeDateOfEvent = this.onChangeDateOfEvent.bind(this);
    this.onChangeStartTime = this.onChangeStartTime.bind(this);
    this.onChangeEndTime = this.onChangeEndTime.bind(this);
    this.saveTutorial = this.saveTutorial.bind(this);
    this.newTutorial = this.newTutorial.bind(this);

    this.state = {
      id: null,
      title: "",
      description: "",
      venueName: "",
      venueAddress: "",
      dateOfEvent: "",
      startTime: "",
      endTime: "",
      published: false,

      submitted: false
    };
  }

  onChangeTitle(e) {
    this.setState({
      title: e.target.value
    });
  }

  onChangeDescription(e) {
    this.setState({
      description: e.target.value
    });
  }
  // 
  onChangeVenueName(e) {
    this.setState({
      venueName: e.target.value
    });
  }

  onChangeVenueAddress(e) {
    this.setState({
      venueAddress: e.target.value
    });
  }

  onChangeDateOfEvent(e) {
    this.setState({
      dateOfEvent: e.target.value
    });
  }

  onChangeStartTime(e) {
    this.setState({
      startTime: e.target.value
    });
  }

  onChangeEndTime(e) {
    this.setState({
      endTime: e.target.value
    });
  }

  // 
  saveTutorial() {
    var data = {
      title: this.state.title,
      description: this.state.description,
      venueName: this.state.venueName,
      venueAddress: this.state.venueAddress,
      dateOfEvent: this.state.dateOfEvent,
      startTime: this.state.startTime,
      endTime: this.state.endTime
    };

    TutorialDataService.create(data)
      .then(response => {
        this.setState({
          id: response.data.id,
          title: response.data.title,
          description: response.data.description,
          venueName: response.data.venueName,
          venueAddress: response.data.venueAddress,
          dateOfEvent: response.data.dateOfEvent,
          startTime: response.data.startTime,
          published: response.data.published,

          submitted: true
        });
        console.log(response.data);
      })
      .catch(e => {
        console.log(e);
      });
  }

  newTutorial() {
    this.setState({
      id: null,
      title: "",
      description: "",
      venueName: "",
      venueAddress: "",
      dateOfEvent: "",
      startTime: "",
      endTime: "",
      published: false,

      submitted: false
    });
  }

  render() {
    return (
      <div className="submit-form">
        {this.state.submitted ? (
          <div>
            <h4>You submitted successfully!</h4>
            <button className="btn btn-success" onClick={this.newTutorial}>
              Add
            </button>
          </div>
        ) : (
          <div>
            <div className="form-group">
              <label htmlFor="title">Event Name</label>
              <input
                type="text"
                className="form-control"
                id="title"
                required
                value={this.state.title}
                onChange={this.onChangeTitle}
                name="title"
              />
            </div>

            {/*  */}

            <div className="form-group">
              <label htmlFor="venueName">Venue Name</label>
              <input
                type="text"
                className="form-control"
                id="venueName"
                required
                value={this.state.venueName}
                onChange={this.onChangeVenueName}
                name="venueName"
              />
            </div>

            <div className="form-group">
              <label htmlFor="venueAddress">Venue Address</label>
              <input
                type="text"
                className="form-control"
                id="venueName"
                required
                value={this.state.venueAddress}
                onChange={this.onChangeVenueAddress}
                name="venueAddress"
              />
            </div>

            <div className="form-group">
              <label htmlFor="dateOfEvent">Date Of Event</label>
              <input
                type="text"
                className="form-control"
                id="dateOfEvent"
                required
                value={this.state.dateOfEvent}
                onChange={this.onChangeDateOfEvent}
                name="dateOfEvent"
              />
            </div>

            <div className="form-group">
              <label htmlFor="startTime">Start Time</label>
              <input
                type="text"
                className="form-control"
                id="startTime"
                required
                value={this.state.startTime}
                onChange={this.onChangeStartTime}
                name="startTime"
              />
            </div>
            <div className="form-group">
              <label htmlFor="endTime">End Time</label>
              <input
                type="text"
                className="form-control"
                id="endTime"
                required
                value={this.state.endTime}
                onChange={this.onChangeEndTime}
                name="endTime"
              />
            </div>

            {/*  */}

            <div className="form-group">
              <label htmlFor="description">About The Event</label>
              <input
                type="text"
                className="form-control"
                id="description"
                required
                value={this.state.description}
                onChange={this.onChangeDescription}
                name="description"
              />
            </div>

            <button onClick={this.saveTutorial} className="btn btn-success">
              Submit
            </button>
          </div>
        )}
      </div>
    );
  }
}
